function Lista2_1format longclearclca=2;b=3;fa=funcao(a);fb=funcao(b);erro=10^-7;k=0;fxm=1;  while (abs(fxm)>erro)    k=k+1;    xm=(a+b)/2;    fxm=funcao(xm);    if (fa*fxm>0)      a=xm;      fa=fxm;    else      b=xm;      fb=fxm;    endif   endwhileprintf("\n-------------------------------\n")fxmxmkprintf("---------------------------------\n")a=2;b=3;erro=10^-7;k=0;x0=2;fx=funcao(x0);dfx=derivada(x0);  while (abs(fx)>erro)    k=k+1;    printf("\n-------------------------------\n")    x0    xk=x0-fx/dfx;    abs(x0-xk)    x0=xk;    fx=funcao(x0);    dfx=derivada(x0);    xk    k    fx    printf("---------------------------------\n")    pause  endendfunction  function f=funcao(x)  f=(x^2)-5;  endfunction  function f1=derivada(x)  f1=2*x;  endfunction