function Sistemas_1_5%Resolução apenas com a matriz final aformat longclearclca=[4 2 3;2 -4 -1;-1 1 4];b=[7 1 -5];n=length(b);  for k=1:n    for i=k:n    soma=0;      for t=1:k-1      soma=soma+a(i,t)*a(t,k);      end    a(i,k)=a(i,k)-soma;    end  for j=k+1:n  soma=0;    for t=1:k-1    soma=soma+a(k,t)*a(t,j);    end    a(k,j)=(a(k,j)-soma)/a(k,k);  end  enda%-------Substituicao Direta Ly = b----------y(1) = b(1)/a(1,1);for i=2:n  soma=0;  for j=1:i-1    soma=soma+a(i,j)*y(j);  endfor  y(i) = (b(i)-soma)/a(i,i);endfory%-------Retrosubstutuicao Ux = y -----------x(n) = y(n);for i = n-1:-1:1 % o termo -2 é para decrementar  soma=0;  for j = i+1:n    soma = soma+a(i,j)*x(j);  endfor  x(i) = (y(i)-soma);endforx%--Resíduo---r = abs(b'-a*x')